// alle Studenten haben 4 Fächer ?
function fertigeStudenten(studenten){
	var result = 0;
	for(var student in studenten){
		student = studenten[student];
		if(student.faecherbelegt.length >= 4){
			if(student.faecherbelegt.length > 4) {
				gs.error("CLE hier läuft was schief, man soll nur 4 fächer bekommen");
