	return result;
}

var armeStudenten = workflow.scratchpad.armeStudenten;
var wahlmodule    = workflow.scratchpad.wahlmodule;
var studenten     = workflow.scratchpad.studenten;

// falls jetzt ein paar arme studenten nicht ihre priorisierten Fächer bekommen haben, werden denen
// irgendwelche restplätze in anderen Modulen zugeordnet, solange nicht schon alle module voll sind.
studentloop:
	for(var student in armeStudenten){
		gs.error("CLE TEST " + armeStudenten[student]);
		student = studenten[armeStudenten[student]];
		if(student.faecherbelegt.length < 4) {
			for(var wahlmodul in wahlmodule) {
				wahlmodul = wahlmodule[wahlmodul];
				if(wahlmodul.frei > 0){
					student.faecherbelegt.push(wahlmodul.sysid);
					wahlmodul.frei--;
				}
				if(student.faecherbelegt.length == 4) {
					continue studentloop;	
				}
			}
		}
		if(student.faecherbelegt.length < 4) {
			gs.error("ACHTUNG: Mehr Studenten als Plätze in den Modulen.");
			break studentloop;	
		}
	}

