function nextStudent(){
	workflow.scratchpad.aktStudNr %= anzStudenten;
	var count = 0;
	for(var student in studenten){
		student = studenten[student];
		if(count == workflow.scratchpad.aktStudNr) {
			workflow.scratchpad.aktStud = student;
			break;
		}
		count += 1;
	}
}

