var index = student.index;
function ifScript(){
	if(wahlmodule[student.faecherprio[index]].frei > 0) {
		wahlmodule[student.faecherprio[index]].frei -=1;
		student.faecherbelegt.push(student.faecherprio[index]);
		gs.info("CLE Student " + workflow.scratchpad.aktStudNr + " hat seine " + student.index + ". Prioriät bekommen.");
		// zur nächsten Wahl dieses Studenten iterieren
		student.index +=1;
		return 'yes';
	} else {
		// gleicher Student mit nächster Prio ist dran
		return 'no';
	}
}

