(function() {
	// Set the variable 'answer' to a comma-separated list of user or group sys_ids that you want the email sent to.
	var usermails = [];
	
	// get all Users whith prioritys in the actual wahlphase
	var gr_prio_student = new GlideAggregate('x_271560_wahlkurse_prioritaet');
	gr_prio_student.addQuery('u_wahlgang', current.getValue('sys_id'));
	gr_prio_student.groupBy('u_student');
	gr_prio_student.query();
	while(gr_prio_student.next()){
		var gr_student = new GlideRecord('x_271560_wahlkurse_student');
		gr_student.get('sys_id', gr_prio_student.getValue('u_student'));
		usermails.push(gr_student.getValue('u_user'));
	}
	
	// create comma separated list
	answer = usermails.join();
	// return the value to use for Parameter 1
	return answer;
}());

(function() {
	// Set the variable 'answer' to a comma-separated list of user or group sys_ids that you want the email sent to.
	var usermails = [];
	
	// get all Users whith prioritys in the actual wahlphase
	var gr_prio_student = new GlideAggregate('x_271560_wahlkurse_prioritaet');
	gr_prio_student.addQuery('u_wahlgang', current.getValue('sys_id'));
	gr_prio_student.groupBy('u_student');
	gr_prio_student.query();
	while(gr_prio_student.next()){
		var gr_student = new GlideRecord('x_271560_wahlkurse_student');
		gr_student.get('sys_id', gr_prio_student.getValue('u_student'));
		usermails.push(gr_student.getValue('u_user'));
	}
	
	// create comma separated list
	answer = usermails.join();
	// return the value to use for Parameter 1
	return answer;
}());

