sys_variable_value.value:
// speichert alles zu den teilnehmenden Studenten
var studenten = [];
var anzStudenten = 0;

// speichert die ausgeschiedenen Studenten (for future use only)
var studentenmitzuwenigfaechern = [];

// speichert alles zu den Wahlmodulen
var wahlmodule = [];

// erstmal die faecheranz für alle teilnehmenden Studenten auf 0 setzen:
var gr_prios_studenten = new GlideAggregate('x_271560_wahlkurse_prioritaet');
// gr_prios_studenten.addQuery('u_wahlgang', current.getValue('sys_id')); nur ein wahlgang
gr_prios_studenten.addAggregate('COUNT', 'u_student'); // anzahl prios zählen
gr_prios_studenten.groupBy('u_student'); //für jeden studenten
gr_prios_studenten.query();
while(gr_prios_studenten.next()){
	// alle studenten, die mindestens 8 Fächer priorisiert haben berücksichtigen
	if(gr_prios_studenten.getAggregate('COUNT', 'u_student') >= 8){
		// hier das studenten objekt initialisieren
		studenten[gr_prios_studenten.getValue('u_student')] = [];
		studenten[gr_prios_studenten.getValue('u_student')].sysid = gr_prios_studenten.getValue('u_student');
		studenten[gr_prios_studenten.getValue('u_student')].faecherprio = [];
		studenten[gr_prios_studenten.getValue('u_student')].faecherbelegt = [];
		studenten[gr_prios_studenten.getValue('u_student')].index = 0;
		anzStudenten++;
		//gs.error("Student hinzugefügt");
	}else{
		studentenmitzuwenigfaechern.push(gr_prios_studenten.getValue('u_student'));
		//gs.error("Student hat zu wenig priorisiert");
	}
}

// 2. Hashmap speichern Fach:Freie Plätze
var gr_module = new GlideRecord('x_271560_wahlkurse_wahlmodul');
gr_module.addQuery('u_semester', current.getValue('u_semester')); // nur aus dem aktuellen semester
gr_module.query();
while(gr_module.next()){
	// hier wird das wahlmodul objekt initialisiert
	wahlmodule[gr_module.getValue('sys_id')] = [];
	wahlmodule[gr_module.getValue('sys_id')].frei = gr_module.getValue('u_anzahl_teilnehmer');
	wahlmodule[gr_module.getValue('sys_id')].sysid = gr_module.getValue('sys_id');
	//gs.error("Wahlmodul hinzugefügt");
}

// hier alle Plätze, die schon belegt sind rauslöschen
//-> nicht notwendig, da wir nur von einem Wahlgang pro Semester ausgehen
/*var gr_zuordnung_module = new GlideAggregate('x_271560_wahlkurse_m2m_wahlmodul_student');
gr_zuordnung_module.addAggregate('COUNT', 'wahlmodul');
gr_zuordnung_module.groupBy('wahlmodul');
gr_zuordnung_module.query();
while(gr_zuordnung_module.next()){
	wahlmodule[gr_zuordnung_module.getValue('wahlmodule')].frei -=
		gr_zuordnung_module.getAggregate('COUNT', 'wahlmodul');
}*/

// 3. für jeden Studenten die priorisierten fächer nach priorität absteigend sortieren und in Maps
// speichern
//	Student : liste von fächern
for (var student in studenten){
	//gs.error("Priorisieren für " + student);
	student = studenten[student];
	var gr_prio = new GlideRecord('x_271560_wahlkurse_prioritaet');
	// gr_prio.addQuery('u_wahlgang', current.getValue('sys_id')); es gibt nur einen wahlgang
	gr_prio.addQuery('u_student', student.sysid);
	gr_prio.orderByDesc('u_prioritaet');
	gr_prio.query();
	while(gr_prio.next()){
		//gs.error("Modul priorisiert.");
		student.faecherprio.push(gr_prio.getValue('u_wahlmodul'));
	}
}

// jetzt die priorisierten fächer belegen
// möglichst fair: jeder Student nimmt sich immer erstmal eins, wenn alle eins haben, dann das nächste usw.
var index = 0;
var armeStudenten = []; // arm, weil alle priofächer schon belegt sind
while(!alleVoll(wahlmodule) && fertigeStudenten(studenten) + armeStudenten.length < anzStudenten){
	for(var student in studenten){ // durch die studenten durch gehen
		student = studenten[student];
		index = student.index;
		while(index < student.faecherprio.length){ // solange noch nicht das ende der liste erreicht ist
			if(wahlmodule[student.faecherprio[index]].frei > 0){ // das nächste freie fach aus der liste hinzufügen
				wahlmodule[student.faecherprio[index]].frei -= 1; // freie plätze runterzählen
				student.faecherbelegt.push(student.faecherprio[index]); // zu belegten fächer hinzufügen
				student.index +=1; // index weiterzählen
				gs.error("Modul belegt!");
				break; // damit erstmal der nächste Student dran ist
			}else{
				student.index +=1;
			}
		}
		if(index == student.faecherprio.length && student.faecherbelegt.length < 4){
			armeStudenten.push(student.sysid);
		}
	}
}

// falls jetzt ein paar arme studenten nicht ihre priorisierten Fächer bekommen haben, werden denen
// irgendwelche restplätze in anderen Modulen zugeordnet, solange nicht schon alle module voll sind.
for(var student in armeStudenten){
	student = studenten[student];
	while(!alleVoll(wahlmodule) && student.facherbelegt.length < 4){
		for(var wahlmodul in wahlmodule){
			if(wahlmodul.frei > 0){
				student.faecherbelegt.push(student.faecherprio(index));
				wahlmodul.frei--;
			}
		}
	}
}


workflow.scratchpad.studenten = studenten;
workflow.scratchpad.wahlmodule = wahlmodule;

// alle fächer voll ?
function alleVoll(wahlmodule){
	for(var wahlmodul in wahlmodule){
		wahlmodul = wahlmodule[wahlmodul];
		if(wahlmodul.frei > 0){
			gs.error("noch nicht alle voll :)");
			return false;
		}
	}
	gs.error("alle voll :(");
	return true;
}


// alle Studenten haben 4 Fächer ?
function fertigeStudenten(studenten){
	var result = 0;
	for(var student in studenten){
		student = studenten[student];
		if(student.faecherbelegt.length >= 4){
			result += 1;
		}
	}
	gs.error("fertige studenten: " + result);
	return result;
}

// Zuordnung in DB speichern
	/* es scheint besondere Methoden zu geben um auf Many-to-many Tabellen zuzugreifen
	
	var gru = new GRUtil();
	var roles = gru.getM2MList(gr, 'sys_user_has_role', 'user', 'role', 'sys_user_role');
	while (roles.next())
	   gs.log(roles.name);

	ich habe aber leider keine gefunden um cool Records zu erzeugen, also doch mit der Hand: */

for (var student in studenten){
	student = studenten[student];
	gs.error("Student wird eingetragen: " + student.faecherbelegt.length);
	for (var i = 0; i < student.faecherbelegt.length; i++){
		gs.error("Modul eingetragen!");
		var gr_studentWahlmodul = new GlideRecord('x_271560_wahlkurse_m2m_wahlmodul_student');
		gr_studentWahlmodul.initialize();
		gr_studentWahlmodul.setValue('student', student.sysid);
		gr_studentWahlmodul.setValue('wahlmodul', student.faecherbelegt[i]);
		gr_studentWahlmodul.insert();
	}
}

