var student = workflow.scratchpad.aktStud;
var armeStudenten = worklfow.scratchpad.armeStudenten;

answer = ifScript();

function ifScript(){
	index = student.index;
	if(index < student.faecherprio.length) {
		return 'no';
	} else {
		if(student.faecherbelegt.length < 4) {
			gs.error("CLE armer Student Nr: " + workflow.scratchpad.aktStudNr);
			armeStudenten.push(student.sysid);
		}
		gs.error("CLE Student Nr: " + workflow.scratchpad.aktStudNr + " ist fertig!!");
		workflow.scratchpad.aktStudNr +=1;
		return 'yes';
	}
}

