function ifScript(){
	index = student.index;
	if(index < student.faecherprio.length) {
		return 'no';
	} else {
		if(student.faecherbelegt.length < 4) {
			gs.error("CLE armer Student Nr: " + workflow.scratchpad.aktStudNr);
			armeStudenten.push(student.sysid);
		}
