var anzStudenten = workflow.scratchpad.anzStudenten;
var studenten = workflow.scratchpad.studenten;
var anzFertig = 0;
var anzArme = workflow.scratchpad.anzArme;

answer = ifScript();

function ifScript() {
	anzFertig = fertigeStudenten(studenten);
	workflow.scratchpad.anzFertig = anzFertig;
	if (anzFertig+anzArme == anzStudenten) {
		return 'yes';
	}
	nextStudent();
	gs.error("CLE aktueller Student: " + workflow.scratchpad.aktStudNr);
	return 'no';
}

// alle Studenten haben 4 Fächer ?
function fertigeStudenten(studenten){
	var result = 0;
	for(var student in studenten){
		student = studenten[student];
		if(student.faecherbelegt.length >= 4){
			if(student.faecherbelegt.length > 4) {
				gs.error("CLE hier läuft was schief, man soll nur 4 fächer bekommen");
			}
			result += 1;
		}
	}
	gs.error("fertige studenten: " + result);
	return result;
}

function nextStudent(){
	var count = 0;
	for(var student in studenten){
		if(count == workflow.scratchpad.aktStudNr) {
			workflow.scratchpad.aktStud = studenten[student];
			break;
		}
		count += 1;
	}
}

