var student = workflow.scratchpad.aktStud;
var wahlmodule = workflow.scratchpad.wahlmoudle;
var armeStudenten = worklfow.scratchpad.armeStudenten;

answer = ifScript();

function ifScript(){
	if(wahlmodule[student.faecherprio[index]].frei > 0) {
		wahlmodule[student.faecherprio[index]].frei -=1;
		student.faecherbelegt.push(student.faecherprio[index]);
		// zur nächsten Wahl dieses Studenten iterieren
		student.index +=1;
		// zum nächsten Student iterieren
		workflow.scratchpad.aktStudNr +=1;
		gs.error("Student " + workflow.scratchpad.aktStudNr + " hat seine " + student.index + ". Prioriät bekommen.");
		return 'yes';
	} else {
		// gleicher Student mit nächster Prio ist dran
		return 'no';
	}
}

var student = workflow.scratchpad.aktStud;
var wahlmodule = workflow.scratchpad.wahlmoudle;
var armeStudenten = worklfow.scratchpad.armeStudenten;

answer = ifScript();

function ifScript(){
	if(wahlmodule[student.faecherprio[index]].frei > 0) {
		wahlmodule[student.faecherprio[index]].frei -=1;
		student.faecherbelegt.push(student.faecherprio[index]);
		// zur nächsten Wahl dieses Studenten iterieren
		student.index +=1;
		// zum nächsten Student iterieren
		workflow.scratchpad.aktStudNr +=1;
		gs.error("Student " + workflow.scratchpad.aktStudNr + " hat seine " + student.index + ". Prioriät bekommen.");
		return 'yes';
	} else {
		// gleicher Student mit nächster Prio ist dran
		return 'no';
	}
}

var studenten = workflow.scratchpad.studenten;
var wahlodule = workflow.scratchpad.wahlmodule;

// zu jedem modul alle praktika holen und unter wahlmodul.praktika speichern
for(var wahlmodul in wahlmodule){
	wahlmodul = wahlmodule[wahlmodul];
	gs.error("CLE Wahlmodul Nr: " + wahlmodul.sysid);
	wahlmodul.praktika = [];
	var gr = new GlideRecord('x_271560_wahlkurse_praktikum');
	gr.addQuery('u_wahlmodul', wahlmodul.sysid);
	gr.query();
	while(gr.next()){
		wahlmodul.praktika.push(gr.getValue('sys_id'));
	}
}

// alle Wahlmodule und deren Praktika durchgehen und solange bis voll Studenten hinzufügen
for(wahlmodul in wahlmodule){
	// alle Studenten zu diesem Wahlmodul holen
	var gr_student = new GlideRecord('x_271560_wahlkurse_m2m_wahlmodul_student');
	gr_student.addQuery('Wahlmodul', wahlmodul.sysid);
	gr_student.query();
	// Iteration über alle Praktika eines Wahlmoduls
	for(var praktikum in wahlmodul.praktika){
		// Anzahl freie Plätze des Praktikums holen
		var gr_praktikum = new GlideRecord('x_271560_wahlkurse_praktikum');
		gr_praktikum.get(praktikum);
		var freiePlaetze = gr_praktikum.getValue('u_anzahl_teilnehmer');
		// solange Studenten zum Praktikum hinzufügen, wie frei Plätze oder Studenten da sind
		while (gr_student.hasNext() && freiePlaetze > 0){
			gr_student.next();
			// Verknüpfung Praktikum:Student in M2M Tabelle ablegen.
			var gr_praktikumStudent = new GlideRecord('x_271560_wahlkurse_m2m_praktikum_student');
			gr_praktikumStudent.initialize();
			gr_praktikumStudent.setValue('Praktikum', praktikum);
			gr_praktikumStudent.setValue('Student', gr_student.getValue('sys_id'));
			gr_praktikumStudent.insert();
			
			freiePlaetze--;
		}
		// falls das letzte Praktikum nicht komplett voll Studenten ist, 
		// Praktika-Iteration abbrechen und mit nächstem Wahlmodul weiter machen
		if (freiePlaetze){
			break;
		}
	}
	// Falls nicht alle Studenten einen Platz bekommen haben, Fehler loggen. (E-Mail?)
	if (gr_student.hasNext()) {
		gs.error('Mehr Studenten als Praktikumsplätze für Wahlmodul' + gr_student.wahlmodul.getValue('u_titel'));
	}
}

