// alle fächer voll ?
function alleVoll(wahlmodule){
	for(var wahlmodul in wahlmodule){
		wahlmodul = wahlmodule[wahlmodul];
		if(wahlmodul.frei > 0){
			gs.error("noch nicht alle voll :) " + workflow.scratchpad.countwahl);
			return 'no';
		}
	}
	gs.error("alle voll :( " + workflow.scratchpad.countwahl);
	return 'yes';
}

